import React from "react";
import { Link } from "react-router-dom";
import './Profile.css';
import { useEffect, useState } from "react";
import { defaultImgs } from "../defaultimgs";
import MessageInFeed from "../components/MessageInFeed";
import { useMoralis } from "react-moralis";
const Profileothers = () => {
  const { Moralis} = useMoralis();
  const [results, setresults] = useState();
  const user = Moralis.User.current();
  async function getuser(){
    const query = new Moralis.Query("_User")
    query.equalTo("ethAddress",localStorage.getItem("chp"))
    const resultsi = await query.find()
    setresults(resultsi);
   
  }
  getuser()
  let rendtrue = 0
  async function follow(id){
    if(rendtrue == 1){
      let usersend = id
      const queryt = new Moralis.Query("_User")
      if(user.attributes.ethAddress != usersend){
       queryt.equalTo("ethAddress", user.attributes.ethAddress)
       const resultst = await queryt.find()
       resultst.map((ei) =>{
         let following = ei.attributes.following
         let followinglist = following.split("§")
         if(followinglist.includes(usersend)){
           const index = followinglist.indexOf(usersend);
           if (index > -1) {
             followinglist.splice(index, 1);
           }
           user.set("following", followinglist.join("§"))
           user.save()
            document.getElementById(id).innerText="following";
            
         }else{
           followinglist.push(usersend)
           user.set("following", followinglist.join("§"))
           user.save()
           
            document.getElementById(id).innerText="unfollowed";
            
         }
       })
      }else{
        alert("Can't follow yourself!")
      }
}
    }
   setTimeout(() => {
     rendtrue = 1
   }, 1000);
  
  return (
    <>
    {results?.map((e) => {
        return (
            <>
            <div className="pageIdentify">Profile</div>
            <img alt="profilebanner" id="profileBanner" className="profileBanner" src={e.attributes.banner ? e.attributes.banner : defaultImgs[1]}></img>
            <div className="pfpContainer">
              <img alt="profilepfp" id="profilePFP" className="profilePFP" src={e.attributes.pfp ? e.attributes.pfp : defaultImgs[0]}></img>
              <div id="profileName" className="profileName">{e.attributes.username.slice(0, 6)}</div>
              <div className="profileWallet">{`${e.attributes.ethAddress.slice(0, 4)}...
                    ${e.attributes.ethAddress.slice(38)}`}</div>
             
                  <div id={e.attributes.senderAccc} onClick={()=>follow(e.attributes.senderAccc)} className="profileEdit">Follow</div>
              
              <div id="profileBio" className="profileBio">
              {e.attributes.bio}
              </div>
              <div className="profileTabs">
                  <div className="profileTab">
                  Their Messages
                  </div>
              </div>
            </div>
            <MessageInFeed profile={false} other={true}  accountother={e.attributes.ethAddress}></MessageInFeed>
            </>
        )
    })}
    </>
  );
};
export default Profileothers;
